import React, { createContext, Component} from 'react'

export const itmContext = createContext(null)

export class CartContextProvider extends Component {
    constructor(props) {
      super(props)
      this.state = {
         first :0
      }
    }

    increment = ()=>{
        this.setState(prev=>({
            first : prev.first + 1})
        )
        console.log(this.state.first)

    }

    decrement = ()=>{
        this.setState(prev=>({
            first : prev.first + 1})
        )
        console.log(this.state.first)
    }

  render() {
    const qtt = this.state.first
    const vvues = {qtt,increment,decrement}
    return (
      <itmContext.Provider value={vvues}>
        {this.props.children}
      </itmContext.Provider>
    )
  }
}
